mapper.select:
SELECT * FROM ( SELECT rownum rn,b.* from ( select distinct N.NNO,N.TITLE,N.CONTENT,n.category,n.img as image,n.price,n.writer,n.regdate,n.updatedate FROM P_NIJI N LEFT OUTER JOIN P_NIJI_TAG T ON N.NNO = T.NNO WHERE
AND rownum <= #{pageNum} * #{amount} ) b
) WHERE rn > (#{pageNum} - 1) * #{amount}


SELECT NNO,TITLE,CONTENT,CATEGORY,IMG as image,PRICE,WRITER,REGDATE,UPDATEDATE
FROM DEV.P_NIJI
WHERE NNO = #{nno}
